-- TESTOVACIE DATA

INSERT INTO meal (sName, sInstructions)
VALUES
    ("Rýžové placičky s jablkem a banánem", "Uvaříme rýži v osolené vodě. 1/2 (+-) jablka nastrouháme, 1/2 banánu rozmačkáme a smícháme společně s uvařenou rýží a 1 ks vejce. Získáme směs, ze které vytvarujeme na pečící papír placičky a posypeme každou rozdrcenými mandlemi. Pečeme na 200 stupňů 20 minut."),
    ("Osvěžující borůvkový bowl", "Borůvky MRAŽENÉ a kefírové mléko rozmixujeme v mixéru. Dáme do misky a posypeme mandlemi, vlašskými ořechy, semínky a 1/2 banánu."),
    ("Kváskový chléb s budapešťskou pomazánkou", "1 širší plátek kváskového chleba potřeme budapešťskou pomazánkou -> 1/2 tvarohu (zbytek Kubko), 1 lžíce másla, 1/4 zakysané smetany, nakrájená cibulka dle potřeby, kapie (program je nenabízí, proto červená paprika). Červená mleté paprika, sůl, pepř přidáme dle svých chutí."),
    ("Smoothie", "Vše rozmixujeme. 125g tvaroh = 1/2 60g banán = 1/2"),
    ("Banánové smoothie bowl", "Mléko (může být živočišné), kakao, chia a banán (1/2) společně umixujeme do hladka. Směs přemístíme do misky, posypeme nastrouhanou čokoládou (1 ks), ořechy a poklademe plátky banánu (1/2).
Ihned podáváme."),
    ("Jáhly s mandarinkami", "Jáhly uvaříme a smícháme je 1 ks hořké čokolády, aby se rozpustila. Ozdobíme mandarinkami z plechovky, semínky a ořechy."),
    ("Ovesná kaše s jogurtem", "V kastrůlku ohřejeme mléko, přidáme do něj 1 ČL chia semínek, necháme si zároveň převařit vodu v konvici. Až bude mléko teplé, přidáte ovesné vločky, zamícháme a přidáme vodu dle potřeby (přidáváme po troškách, ať ji není příliš, kaše pak nebude mít dobrou chuť), mícháme do zhoustnutí. V misce poté kaši smícháme s celým jogurtem 5% tuku. Ozdobíme ovocem a ořechy."),
    ("Kváskový chléb s hummusem, šunkou a sýrem", "1 plátek chleba potřeme hummusem, obložíme 2 ks vysokoprocentní šunka a 1 plátek sýru. K tomu ovoce a 1 ks mrkev."),
    ("Kváskový chléb s hermelínovou pomazánkou", "V první řadě si dáme vařit vajíčko. Mezitím si pokrájíme hermelín na malé kousky (zbytek Kubko), přidáme pokrájené kyselé okurky a dochutíme 1/2 jogurtem a špetkou soli, pepřem ... Na závěr přidáme pokrájené uvařené vajíčko, a všechno společně promícháme a dáme na chvíli vychladit do lednice. Pomazánkou potřeme kváskový chléb."),
    ("Těstovinový salát se sušenými rajčaty a mozzarellou", "Uvaříme těstoviny (v hotovém +- 85 g), smícháme se sušenými rajčaty (vč. trocha oleje), 1/2 ks mozzarelly (druhá polovina Kubko), listovou zeleninu (nevážíme, odhadem), 1/2 okurka, 1 ks mrkev a semínka, které opražíme na suché pánvi pro lepší chuť. Na konec trošku zakápneme za studena lisovaným olejem. Salát ochutíme dle svých chutí."),
    ("Poke bowl", "Uvaříme rýži (v hotovém stavu +-110g), přendáme do misky a naskládáme na ni čerstvého lososa, avokádo, 1/2 mrkev a kukuřici. Můžeme lehce pokapat sójovou omáčkou."),
    ("Karbanátky z hovězího masa a zeleniny", "Hovězí mleté maso smícháme s 1 ks vejce, 1/2 červené papriky, nastrouhanou 1/2 ks mrkve, 1/2 malé nastrouhané cukety. Osolíme, opepříme, přidáme případně bylinky. Vznikne nám
směs, ze které na pečící papír vytvarujeme placky. Pečeme na 200 stupňů, 30 minut. Ke konci pečení potřeme olivovým olejem."),
    ("Cuketové placky s tuňákem", "Nejprve si oloupej cuketu a najemno nastrouhej. Posol a nech 10 minut odstát.Mezitím si nakrájej cibuli (nemusí být) na kostičky a česnek na plátky. Na řepkovém (můžeš kokosový) oleji restuj dozlatova. Cuketu důkladně vymačkej. Poté přidej vejce (2ks), orestovanou cibuli s česnekem, špaldovou mouku a tuňáka (můžeš i nemusíš dávat do těsta), kterého jsi předem zbavila šťávy. Směs osol, opepři a důkladně zamíchej. Orestuj na pánvi do zlatova.TIP: Placky je možné dát i do trouby. Stačí péct při 200 stupních z každé strany cca. 10 minut"),
    ("Tofu s rýžovými nudlemi", "Rýžové nudle dáme vařit do osolené vody (dle návodu na obalu). Nakrájené tofu
na kostičky lehce osmahneme na sucho na pánvičce na mírném ohni. V mističce si připravíme zálivku: med, citron, olej, sůl. Uvařené nudle chvíli orestujeme spolu s tofu, smícháme, přendáme na talíř/do misky, zalejeme zálivkou, smícháme a též smícháme s 1ks nastrouhanou mrkví."),
    ("Kváskový chléb s quacamole", "Avokádo rozmačkáme v misce, přidáme nakrájenou červenou cibuli, 1/2 balení
cherry rajčátek. Zakápneme citronovou šťávou, osolíme, zamícháme. Podáváme jako pomazánku na 1 plátek žitný chléb."),
    ("Boloňské špagety", "Mleté maso osmahneš na suché pánvi, až bude hotové přidáš boloňskou omáčku, necháš ohřát, přidáš uvařené špagety (150g +- v hotovém) a na talíři posypeš nastrouhaným sýrem."),
    ("Quesadilla s mozzarellou a zeleninou", "Na talíři si rozprostřeme 1 ks tortilly (pozor, té menší!, jsou i velikosti o 120g), naskládáme na její jednu půlku na sebe ledový salát, nakrájená rajčátka, kukuřici, nakrájenou na plátky light mozzarellu a druhou polovinou tortilly směs zaděláme a dáme na kontaktní gril, do toustovače, na pánev či do kleští na gril a následně do trouby a na pár minutek (1-3) z obou stran dáme opéct. K tomu 1/2 kelímek řeckého jogurtu. Buď potřeme nebo do jogurtu namáčíme."),
    ("Tortilla s hummusem a míchaný, vajíčkem", "Na 1ks menší tortilly (před si ji ohřejeme v mikrovlnce 20s) rozetřeme hummus. Na pánvičce potřené kokosovým olejem osmahneme 1ks vejce (míchané). To rozložíme též do tortily na její prostředek, dále přidáme ledový salát, rajčátka a nastrouhanou mrkev."),
    ("Opečené tofu s pečenými brambory, dip ze zakysané smetany", "Pokrájené a očistěné brambory (lze zaměnit za batáty) zakápneme trochou olivovým olejem, přidáme sůl a bylinky (libovolně) a pečeme v troubě dozlatova. Podáváme s na pánvi opečeným tofu (na sucho) a dipem ze zakysky (libovolně přidáme česnek, sůl, pepř, bylinky). K
tomu dále 1ks rajče."),
    ("Čočka se šmakounem a vejcem", "Uvaříme čočku, 2 ks vejce. Na lehce potřené pánvi olejem osmahneme 1 ks šmakouna, druhý ks Kubko."),
    ("Pečený losos s pečenými batáty", "Lososa potřeme na pečícím papíru olejem, ochutíme dle svých chutí (sůl, citron, bylinky...) a přihodíme k němu batáty (lze zaměnit s bramborami)."),
    ("Avokádové špagety se sušenými rajčaty", "umixujeme dohladka avokádo , ochutíme ho solí , pepřem nebo chilli a trošičkou citronové šťávy, ale opravdu jen pár kapek pro osvěžení chuti. V malé pánvičce si opečeme nakrájená rajčátka, jelikož jsou již v oleji a avokádo je také nabité tukem, opečeme je bez dalšího přidávání oleje nebo másla. Poté ztlumíme teplotu, vmícháme avokádo, nastrouhaný sýr a čerstvou bazalku. Vhodíme uvařené těstoviny a vše smícháme, můžeme trošku naředit vodou z těstovin. Podáváme ozdobené bazalkou."),
    ("Bramboráčky", "Brambory oloupeme, nastrouháme a smícháme s 1 ks vejce, ovesnými vločkami, nakrájenou kvalitní zauzenou šunkou/salámem na malé kostičky a stroužkem česneku. Směs ochutíme majoránkou a dle potřeby sůl + pepř. Ze směsi na pečící papír vytvarujeme kolečka. Pečeme na 200 stupňů 30 minut."),
    ("Langoše ze špaldové mouky a kefíru", "Smícháme mouku, kefír a 1 PL olivového oleje. Vytvarujeme na pečící papír menší placičky, pečeme 15 minut na 180 stupňů. Po upečení potřeme česnekem (ale nemusíš), zakysanou smetanou a posypeme nastrouhaným eidamem. Jsou to spíše takové jako langoše, placky se nafouknou, tak se nelekat a pochutnat si i tak. :)"),
    ("Zapečená řepa s nivou a vlašskými ořechy", "pečící papír, posypeme každé kolečko nastrouhanou nivou a na
každé kolečko dáme vlašský ořech. Poté dáme do trouby zapéct na 180 stupňů a vyndáme až bude niva do zlatova (cca 30 minut)."),
    ("Párky s kváskovým chlebem", "2 nožičky kvalitních párků s 1 plátkem kváskového chleba s hořčicí (klidně kečup, záleží co máš ráda). K tomu zelenina."),
    ("Vepřové ražniči s bramborami", "Na plech rozprostřeme alobal, vyskládáme na plech (alobal) nakrájené brambory na tenké plátky a vrstvíme maso též nakrájené na plátky, dále přidáme nakrájenou anglickou slaninu (2 ks)
a papriku. Nakonec navrstvíme hodně cibule, posypeme grilovacím kořením, zakryjeme alobalem a pečeme na 200°C asi 60 minut."),
    ("Kváskový chléb se šunkou a sýrem", "1 plátek kváskového chleba potřeme Lučinou, obložíme 2 ks plátky vysokoprocentní šunky, 1 plátkem sýru. K tomu 1ks mrkev a připíjíme 300 ml kefíru."),
    ("Quinoa salát s halloumi sýrem", "Quinoa uvedena v syrovém stavu (v hotovém +- 90g). Brokolici můžeme dát
na 5 minut vařit. Na ghí (nebo kokosovém oleji) osmahneme nakrájené žampiony, tak stejně osmahneme na plátky nakrájený Halloumi sýr (můžeme na jedné pánvi). Na talíři vše buď smícháme nebo vyskládáme vedle sebe, to už záleží na tobě. Na konec přidáme hrst polníčku či jiné listové zeleniny a posypeme semínky."),
    ("Tofu s rýží a kukuřicí", "Tofu nakrájíme na kostičky a osmahneme na sucho na pánvi. Rýže je uvedena v syrovém stavu, v hotovém potom +- 110g. Talíř doplníme dále o kukuřici, nakrájený pórek (od oka) a semínka. Ochutíme dle svých chutí."),
    ("Polotučný tvaroh s kakaem a mandlovým mlékem", "Do misky si dáme celý tvaroh, mléko (může být živočišné!), semínka, kakao a smícháme. Ozdobíme musli a ovocem."),
    ("Těstovinový salát s tuňákem", "Těstoviny v hotovém +- 85g. Šťávu z tuňáka slejeme a vše smícháme dohromady - hrst polníčku či jiné listové zeleniny, rajčata, oříšky, semínka a těstoviny. Na konec zakápneme za studena lisovaným olejem. Ochutíme dle svých chutí."),
    ("Gnochi s bazalkovým pestem a parmazánem", "Uvaříme gnochi, na pánvi mezitím ohřejeme bazalkové pesto a spolu s ním také mírně podusíme cherry rajčátka, uvařené gnochi k němu přidáme, zamícháme a na talíři posypeme parmazánem."),
    ("Míchaná vajíčka s baby špenátem a žitným chlebem", "Na kokosovém oleji osmahneme 2 míchaná vejce, přidáme na pánev hrst baby špenátu - vše mícháme dohromady. K tomu 1 plátek žitného chleba (může být váš domácí) potřený lučinou + přikusujeme 1/2 jablko."),
    ("Hovězí steak s fazolkami a americkými bramborami", "Na pánvi s rozehřátým sádlem na prudko opečeš maso a
poté jej dáš spolu s bramborami do trouby. Brambory si včetně slupky jen omyješ, nakrájíš a dáš na sucho vedle
masa. Na pánvi po mase poté osmahneš fazolky s česnekem - 1 stroužek."),
    ("Omeleta se špenátem a kozím sýrem", "Vajíčka (2) rozmixujeme spolu s cca hrstí špenátu, přidáme sůl a pepř. Smažíme na kokosovém oleji. Hotovou omeletu naplníme kozím sýrem, pokrájenými ředkvičkami a rukolou."),
    ("Tousty", "2 ks toustový chléb (=1 zapečený toust), potřeme lučinou, do toustu: 1 plátek sýr, 2 plátky vysokoprocentní šunky a zapečeme. K tomu 1 červená paprika (+-) a kefírové mléko."),
    ("Čočkový salát s avokádem a grapefruitem", "Avokádo nakrájíme na kostičky, grapefruit zbavíme slupky vč. vnitřních hořkých blanek a zbylou vnitřní dužinu nakrájíme. 1 ks vejce vařené můžeme též nakrájet nebo rozkrojit na půl a na konec na salát položit. Hlávkový salát natrháme a vše vložíme do mísy s vychladlou čočkou (v hotovém 100 g+-). Důkladně smícháme ingredience na zálivku (ocet, med, sůl, pepř, limetka) a zalijeme jím připravený salát."),
    ("Tuňák, avokádo, rajče, žitný chléb", "Do misky si dáme 1 plechovku tuňáka (olej/vodu slijeme), nakrájené
avokádo, sušená rajčata, ochutíme dle svých chutí (sůl, pepř, ..) a k tomu 1 plátek žitného chleba."),
    ("Jogurt s oříšky, ovocem a semínky", "Jogurt smícháme s medem a semínky. Ozdobíme ovocem, oříšky a zdravým müsli."),
    ("Bulgurový salát se zeleninou a cottage sýrem", "Uvařený bulgur (+-130g v hotovém stavu) smícháme s pokrájenou zeleninou, 1/2 cottage sýrem, semínky a dochutíme dle libosti kořením či bylinkami. Na konec zakápneme za
studena lisovaným olejem."),
    ("Grilovaný seitan s pečenou zeleninou", "Brambory, papriku a cibuli očistíme a nakrájíme na menší kousky.
Zeleninu zakápneme olivovým olejem a ochutíme dle svých chutí. Seitan nakrájíme na asi 1 cm silné plátky a též
dochutíme dle svých chutí. Oboje grilujeme nebo pečeme v troubě, zelenina potřebuje více času, seitan méně. K tomu 1/4 zakysané smetany."),
    ("Kváskový chléb s volským okem", "Plátek kváskového chleba potřeme lučinou, obložíme plátkem chedaru, na kokosovém oleji osmahneme volské oko, položíme na chleba. K tomu červená paprika."),
    ("Caprese s toustovým chlebem", "2 ks rajčata a mozzarellu nakrájíme na tenké plátky, zakápneme olivovým olejem a posypeme bazalkou. K tomu přikusujeme 1 ks opečený toustový chléb."),
    ("Červená řepa s balkánským sýrem a zakysanou smetanou", "Vařenou červenou řepu nakrájíme na kostičky, smícháme s nakrájeným balkánským sárem a1/4 zakysané smetany. Můžeme přidat česnek. Dochutíme dle svých vlastních chutí a přikusujeme 2 ks knäckebrot."),
    ("Buritto", ""),
    ("Tuňáková pomazánka s žitným chlebem", ""),
    ("Pohanka s kysaným zelím, tempehem a tahini", "");


